function() {
  var title = "Number of Majors";
  var description = "This program requires either one or two Majors.";

  function validate(plan, programId) {
    var errors = [];
    var n = 0;

    for (var programId in plan.programs) {
      var program = plan.programs[programId];
      if (program['type'] == "Major") n++;
    }

    if (n==0) {
      errors.push("You must choose at least one major.");

    } else if (n > 2) {
      errors.push("You must choose 1 or 2 majors, but you currently have "+n+" majors selected.");

    }

    return errors;
  }

  return {
    title: title,
    description: description,
    validate: validate
  }
}

function() {
  var title = "Cumulative GPA above 2.0";
  var description = "This program requires a cumulative grade point average (GPA) of at least a 2.00.";

  function validate(plan, programId) {
    var errors = [];
    var n = 0;

    var standardMessage = "Your cumulative grade point average (GPA) must be at least 2.00.";
    var manualCheck = "MANUAL-CHECK: ";

    if ('SUMMARY_DATA' in plan && 'CUMULATIVE GPA' in plan['SUMMARY_DATA']) {

      var gpa = parseFloat(plan['SUMMARY_DATA']['CUMULATIVE GPA']);
      var gpa_note = " (It is currently "+gpa+".)";

      if (isNaN(gpa)) {
        errors.push(manualCheck + standardMessage);

      } else if (gpa < 2.0) {
          errors.push(standardMessage + gpa_note);

      } else if (gpa < 2.5) {
        errors.push(manualCheck + standardMessage + gpa_note);

      }

    } else {
      errors.push(manualCheck + standardMessage);

    }


    return errors;
  }

  return {
    title: title,
    description: description,
    validate: validate
  }
}

function() {
  var title = "Number of Credits";
  var description = "This program requires completion of at least 120 undergraduate credits.";

  function validate(plan, programId) {
    var errors = [];
    var total_credit_units = 0;
    var transfer_credit_units = 0;

    for (var semesterName in plan.schedule) {
      var semester = plan.schedule[semesterName];

      for (var courseName in semester.courses) {
        var course = semester.courses[courseName];

        var courseCodeNumber = course['course_code'].match(/\d+/g).map(Number)[0];
        var firstGraduateLevelCourseCodeNumber = 200;

        if (courseCodeNumber < firstGraduateLevelCourseCodeNumber) {

          if (course['attempted']) {
            total_credit_units += parseFloat(course['attempted']);

            if (course['course_title'] == "Transfer Credits") {
              transfer_credit_units = parseFloat(course['attempted'])
            }
          }

          if (course['credit_units']) {
            total_credit_units += parseFloat(course['credit_units']);

            if (course['course_title'] == "Transfer Credits") {
              transfer_credit_units = parseFloat(course['credit_units'])
            }
          }
        }
      }
    }

    if (total_credit_units < 120) {
      errors.push("You currently only have "+total_credit_units+" undergraduate credits in your plan.");

    } else if ((total_credit_units - transfer_credit_units) > 120) {
      errors.push("MANUAL-CHECK: You currently have over 120 undergraduate credit units in your Course Plan. Minerva tuition covers a maximum of 120 undergraduate credit units over four years. Additional course fees apply for any additional credit units (excluding transfer credits.)");

    }

    return errors;
  }

  return {
    title: title,
    description: description,
    validate: validate
  }
}

