function() {
  var title = 'Full-Time Enrollment Status';
  var description = 'This program requires maintaining full-time enrollment status.';

  function validate(plan, programId) {
    var errors = [];

    for (var semesterName in plan.schedule) {
      var semester = plan.schedule[semesterName];

      var creditsUnitsInTerm = 0;
      var semester_completed = false;

      for (var courseName in semester.courses) {
        var course = semester.courses[courseName];

        if (course['credit_units']) {
          creditsUnitsInTerm += parseFloat(course['credit_units']);

        } else if (course['attempted']) {
          creditsUnitsInTerm += parseFloat(course['attempted']);
        }

        if (course['from_transcript']) {
          semester_completed = true;
        }
      }

      if (creditsUnitsInTerm > 0) {

        if (!semester_completed) {

          var termTitle = semesterName.replace(/-/g, " ");

          if (semester['term_index'] < 2) {
            // Year 1.
            if (creditsUnitsInTerm < 16) {
              errors.push("MANUAL-CHECK: In the "+termTitle+" term, you have scheduled "+creditsUnitsInTerm+" credit units, which is below the standard minimum for Full-Time Enrollment for First Year students of 16 credit units. This is permissible only under certain conditions, as outlined in the <A HREF=\"https://hub.minerva.kgi.edu/student-center/student-affairs/student-handbook/#course load\">Student Handbook.</A>");
            } else if (creditsUnitsInTerm > 16) {
              errors.push("MANUAL-CHECK: In the "+termTitle+" term, you have scheduled "+creditsUnitsInTerm+" credit units, which is above the standard maximum for Full-Time Enrollment for First Year students of 16 credit units. This is permissible only under certain conditions, as outlined in the <A HREF=\"https://hub.minerva.kgi.edu/student-center/student-affairs/student-handbook/#course load\">Student Handbook.</A>");
            }

          } else if (semester['term_index'] < 10) {
            // Subsequent years
            if (creditsUnitsInTerm < 12) {
              errors.push("MANUAL-CHECK: In the "+termTitle+" term, you have scheduled "+creditsUnitsInTerm+" credit units, which is below the standard minimum for Full-Time Enrollment for Upper-Division students of 12 credit units. This is permissible only under certain conditions, as outlined in the <A HREF=\"https://hub.minerva.kgi.edu/student-center/student-affairs/student-handbook/#course load\">Student Handbook.</A>");
            } else if (creditsUnitsInTerm > 16) {
              errors.push("MANUAL-CHECK: In the "+termTitle+" term, you have scheduled "+creditsUnitsInTerm+" credit units, which is above the standard maximum for Full-Time Enrollment for Upper-Division students of 16 credit units. This is permissible only under certain conditions, as outlined in the <A HREF=\"https://hub.minerva.kgi.edu/student-center/student-affairs/student-handbook/#course load\">Student Handbook.</A>");
            }
          }
        }

      }

    }
    return errors;
  }


  return {
    title: title,
    description: description,
    validate: validate
  }
}

function() {
  var title = 'Number of Non-Course Credits';
  var description = 'This program allows for no more than a total of 8 credits from Transfer Credits, Prior Experiential Learning, and Summer Research / Internship Experience.';


  function validate(plan, programId) {
    var errors = [];
    var non_course_credits = 0;

    var semester = plan.schedule["Other"];

    for (var courseName in semester.courses) {
      var course = semester.courses[courseName];

      if (course['attempted'] && course['course_type'] == "Non-Course Credit") {
        non_course_credits += parseFloat(course['attempted'])
      }

      if (course['credit_units'] && course['course_type'] == "Non-Course Credit") {
        non_course_credits += parseFloat(course['credit_units'])
      }
    }

    if (non_course_credits > 8) {
      errors.push('This program allows for no more than a total of 8 credits from Transfer Credits, Prior Experiential Learning, and Summer Research / Internship Experience.');
    }

    return errors;
  }

  return {
    title: title,
    description: description,
    validate: validate
  }
}

function() {
  var title = 'Number of Concentrations per Major';
  var description = 'This program requires completion of at least one Concentration within the Major.';

  function validate(plan, programId) {
    var errors = [];

    var selectedProgram = plan.programs[programId];

    var childConcentrationCount = 0;

    for (var otherProgramId in plan.programs) {
      var otherProgram = plan.programs[otherProgramId];

      if (otherProgram['type'] == "Concentration" &&
          otherProgram['parent_program'] &&
          otherProgram['parent_program']['id'] == programId) {
            childConcentrationCount += 1;
      }
    }

    if (childConcentrationCount == 0) {
      errors.push('You must add at least one Concentration within the ' + selectedProgram['name'] + ' ' + selectedProgram['type'] + ' to your plan.');
    }

    return errors;
  }

  return {
    title: title,
    description: description,
    validate: validate
  }
}

function() {
  var title = "Distribution Requirement for Major";
  var description = "This program requires completion of at least 20 credit units from Core and/or Concentration courses that are offered in Colleges other than this one.";

  function validate(plan, programId) {
    var errors = [];
    var n = 0;

    var program = plan.programs[programId];

    for (var semesterName in plan.schedule) {
      var semester = plan.schedule[semesterName];

      for (var courseName in semester.courses) {
        var course = semester.courses[courseName];

        var credits = 0;

        if (course['credit_units']) {
          credits = parseFloat(course['credit_units']);

        } else if (course['attempted']) {
          credits = parseFloat(course['attempted']);
        }

        if (course['college'] != program['college'] &&
            (course['course_type'] == "Major Core" || course['course_type'] == "Concentration")) {
          n += credits;
        }
      }
    }

    if (n < 20) {
      errors.push('You must complete at least 20 credit units from Core and/or Concentration courses outside of ' + program['college'] + '. Your plan currently includes '+n+' such credits.' );
    }

    return errors;
  }

  return {
    title: title,
    description: description,
    validate: validate
  }
}

